import React from "react";
import Form from "./Form";

class RegisterLogin extends Form {
  state = {
    activegroup: "login",
    inputs: [
      {
        label: "Username",
        type: "text",
        groups: ["login", "register"],
        placeholder: "Username"
      },
      {
        label: "Email",
        type: "text",
        groups: ["register"],
        placeholder: "Email ex. email@email.com"
      },
      {
        label: "Password",
        type: "password",
        groups: ["login", "register"],
        placeholder: "Username"
      }
    ]
  };
  render() {
    return (
      <div>
        <form className="login_register">
          {this.state.inputs.map((input, i) => {
            if (input.groups.indexOf(this.state.activegroup) > -1) {
              return (
                <div key={i}>
                  <label key={i}>{input.label}</label>
                  <input type={input.type} placeholder={input.placeholder} />
                </div>
              );
            }
          })}
        </form>
      </div>
    );
  }
}

export default RegisterLogin;
